import { useState } from 'react';
import data from '../data'
import '../App.css'
function UnitConversion ({ activeUnitType, setActiveUnitType }) {
    const [firstUnit, setFirstUnit] = useState()
    const [secondUnit, setSecondUnit] = useState()

    function handleChange (event) {
        setFirstUnit(event.target.value)
        console.log(`The first unit has been changed to ${event.target.value}`)
        // unit, order
        // if(order === 'first') {
        //     setFirstUnit(event.target.value)
        //     console.log(firstUnit)
        // } else if (order === 'second') {
            // setSecondUnit(unit)
        }

    function formatUnitType (word) {
        let wordArray = word.match(/[A-Z][a-z]*/g)
        let wordString = wordArray.join(' ')
        return wordString
    }

    return (
        <div>
            <form className='form-container'>
                <div>
                <label>From: </label>
                <select name='from' onChange={handleChange} value={firstUnit}>
                    {Object.keys(data.unitTypes[activeUnitType]).map(item => {
                        return (
                            <option key={item} id={item} value={item}>{formatUnitType(item)}</option>
                        )
                    })}
                </select><br/>
                <input type='text'></input>
                </div>
                <div>
                <label>To: </label>
                <select name='to' value={secondUnit}>
                    {Object.keys(data.unitTypes[activeUnitType]).map(item => {
                        return (
                            <option key={item} id={item} value={item}>{formatUnitType(item)}</option>
                        )
                    })}
                </select><br/>
                <input type='text'></input>
                </div>
            </form>
            <div>
                <h3>Result: </h3>
            </div>
        </div>
    )
}

export default UnitConversion;
